css:
.item-table tbody tr:nth-child(odd) {
  background-color: rgba(100, 149, 237, .2);
}

.item-table thead tr:nth-child(even) {
    background-color: unset !important;
    border-bottom: 3px solid #afc9f6 !important;
    font-weight: bold;
}

#no-bgc-table tbody tr:nth-child(odd) {
    background-color: transparent !important;
    padding: 0 !important;
}

#no-bgc-table tbody td {
    padding: 0 !important;
}

.print-format {
    margin-left: 2mm;
    margin-right: 0mm;
}

#main_content {
    padding-left: 55px;
    padding-right: 55px;
}

body {
    padding: 0 0 5mm !important;
}
