css:
@font-face {
  font-family: lato;
  src: url('/assets/energielenker/fonts/lato-v20-latin-regular.woff2');
}

.blue-white:nth-child(odd) {
  background-color: rgb(186, 210, 226) !important;
}

.item-table thead tr:nth-child(even) {
    background-color: unset !important;
    border-bottom: 3px solid rgb(186, 210, 226) !important;
    font-weight: bold;
}

#no-bgc-table tbody tr:nth-child(odd) {
    background-color: transparent !important;
    padding: 0 !important;
}

#no-bgc-table tbody td {
    padding: 0 !important;
}

.print-format {
    font-size: 10pt;
    margin-left: 2mm;
    margin-right: 0mm;
}

#main_content {
    padding-left: 55px;
    padding-right: 55px;
}

body {
    padding: 0 0 5mm !important;
    font-family: lato !important;
}

.avoidBreak { 
    page-break-inside:avoid !important;
}
